package com.rsi.rvia.rest.endpoint.ruralvia.tarjetas;import javax.servlet.http.HttpServletRequest;import javax.ws.rs.Consumes;import javax.ws.rs.GET;import javax.ws.rs.POST;import javax.ws.rs.Path;import javax.ws.rs.Produces;import javax.ws.rs.core.Context;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;import javax.ws.rs.core.UriInfo;import org.json.JSONObject;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import com.rsi.Constants;import com.rsi.rvia.rest.client.OperationManager;/** * The Class Tarjeta Prepago. */@Path("/cards/prepaid")public class TarjetaPrepago{    /** The p log. */    private static Logger pLog = LoggerFactory.getLogger(TarjetaPrepago.class);    /**     * Muestra el listado de tarjetas (Tarifas) que comercializa la entidad para este producto.     *      * @param pRequest     *            the request     * @param pUriInfo     *            the uri info     * @return Objeto que contiene la respuesta y en caso positivo se adjunta el listado las tarjetas prepago     *         disponibles     */    @GET    @Path("/rates")    @Produces({ MediaType.TEXT_HTML })    @Consumes({ MediaType.APPLICATION_XHTML_XML, MediaType.TEXT_HTML, MediaType.APPLICATION_FORM_URLENCODED,            "application/x-ms-application" })    public Response getPrepaidCardListHtml(@Context HttpServletRequest pRequest, @Context UriInfo pUriInfo)    {        pLog.info("Se solicita el template HTML para TarjetaPrepago.");        Response pReturn = OperationManager.processTemplate(pRequest, pUriInfo, true);        pLog.info("Se finaliza la solicitud del template HTML para TarjetaPrepago.");        return pReturn;    }    /**     * Muestra el listado de tarjetas (Tarifas) que comercializa la entidad para este producto.     *      * @param pRequest     *            the request     * @param pUriInfo     *            the uri info     * @return Objeto que contiene la respuesta y en caso positivo se adjunta el listado las tarjetas de Prepago     *         disponibles     */    @GET    @Path("/rates")    @Produces({ MediaType.APPLICATION_JSON })    @Consumes({ MediaType.APPLICATION_JSON })    public Response getPrepaidCardList(@Context HttpServletRequest pRequest, @Context UriInfo pUriInfo)    {        pLog.info("Se obtienen el listado de tarjetas prepago disponibles.");        Response pReturn = OperationManager.processDataFromRvia(pRequest, pUriInfo, "{}", MediaType.APPLICATION_JSON_TYPE);        pLog.info("Se finaliza la obtencion de el listado de tarjetas prepago disponibles.");        return pReturn;    }    /**     * Muestra los detalles de la tarifa seleccionada de la tarjeta prepago, como pueden ser los límites, las     * comisiones, etc.     *      * @param pRequest     *            the request     * @param pUriInfo     *            the uri info     * @return Objeto que contiene la respuesta y en caso positivo se adjunta el detalle de la tarjeta prepago.     */    @GET    @Path("/{idLinea}/{idGrupo}/{idPdv}/{idTrfa}")    @Produces({ MediaType.APPLICATION_JSON })    @Consumes({ MediaType.APPLICATION_JSON })    public Response getPrepaidCardDetail(@Context HttpServletRequest pRequest, @Context UriInfo pUriInfo)    {        pLog.info("Se obtienen el detalle la tarjeta prepago.");        Response pReturn = OperationManager.processDataFromRvia(pRequest, pUriInfo, "{}", MediaType.APPLICATION_JSON_TYPE);        pLog.info("Se finaliza la obtencion de el detalle la tarjeta prepago.");        return pReturn;    }    /**     * El cliente selecciona la cuenta de domiciliación y la imagen visual de la tarjeta. Además de la aceptación de las     * condiciones generales de servicio y la LOPD, así como la introducción de la firma.     *      * @param pRequest     *            the request     * @param pUriInfo     *            the uri info     * @return Objeto que contiene la respuesta de la firma del contrato de tarjeta prepago.     */    @GET    @Path("/{idLinea}/{idGrupo}/{idPdv}/{idTrfa}/contract")    @Produces({ MediaType.APPLICATION_JSON })    @Consumes({ MediaType.APPLICATION_JSON })    public Response getPrepaidCardContract(@Context HttpServletRequest pRequest, @Context UriInfo pUriInfo)    {        pLog.info("Se obtienen los datos relativos a la cuenta domiciliacion, LOPD, imagen tarjeta y también los datos de introducción de la firma de tarjeta prepago.");        Response pReturn = OperationManager.processDataFromRvia(pRequest, pUriInfo, "{}", MediaType.APPLICATION_JSON_TYPE);        pLog.info("Se finaliza la obtencion de los datos relativos a la cuenta domiciliacion, LOPD, imagen tarjeta y también los datos de introducción de la firma de tarjeta prepago.");        return pReturn;    }    /**     * Se trata de la pantalla de respuesta tras la introducción de la firma. El resultado puede ser Procesado     * (Aceptado) o Rechazado.     *      * @param pRequest     *            the request     * @param pUriInfo     *            the uri info     * @return Objeto que contiene la respuesta tras la introducción de la firma.     */    @POST    @Path("/{idLinea}/{idGrupo}/{idPdv}/{idTrfa}/signature")    @Produces({ MediaType.APPLICATION_JSON })    @Consumes({ MediaType.APPLICATION_JSON })    public Response getPrepaidCardSignature(@Context HttpServletRequest pRequest, @Context UriInfo pUriInfo)    {        pLog.info("Se llama a la operativa de firma tarjeta prepago.");        Response pReturn = OperationManager.processDataFromRvia(pRequest, pUriInfo, "{}", MediaType.APPLICATION_JSON_TYPE);        pLog.info("Se finaliza la llamada a la operativa de firma tarjeta prepago.");        return pReturn;    }    /**     * Devuelve PDF del documento del contrato de Tarjeta de Prepago.     *      * @param pRequest     *            the request     * @param pUriInfo     *            the uri info     * @return pdf con contrato de Tarjeta Prepago     */    @GET    @Path("/{idLinea}/{idGrupo}/{idPdv}/{idTrfa}/pdf")    @Consumes({ MediaType.APPLICATION_JSON })    @Produces(Constants.HTTP_HEADER_MEDIATYPE_PDF)    public Response getpdfPrepaid(@Context HttpServletRequest pRequest, @Context UriInfo pUriInfo)    {        try        {            pLog.info("Se obtiene el pdf del documento del contrato de Tarjeta de Prepago");            Response pReturn = OperationManager.processDataFromRvia(pRequest, pUriInfo, "{}", MediaType.APPLICATION_JSON_TYPE);            String strPdfBase64 = (new JSONObject(pReturn.getEntity().toString())).getJSONObject("response").getJSONObject("data").getString("buffer");            byte[] abFile = org.apache.commons.codec.binary.Base64.decodeBase64(strPdfBase64.getBytes());            String strFileName = "Contrato_TarjetaPrepago.pdf";            String strHeaderDownload = "attachment; filename=\"" + strFileName + "\"";            pLog.info("Se finaliza la obtencion de el pdf del documento del contrato de Tarjeta de Prepago");            return Response.ok(abFile, Constants.HTTP_HEADER_MEDIATYPE_PDF).header("Content-Disposition", strHeaderDownload).build();        }        catch (Exception e)        {            return Response.serverError().build();        }    }}